import React, { useEffect, useState } from 'react';
import './styles/Home.css';
import './styles/Mobile.css';
import { Link } from 'react-router-dom';

function Settings() {

    const [feelings, setFeelings] = useState([]);
    const [editingIndex, setEditingIndex] = useState(null);
    const [modifiedFeelings, setModifiedFeelings] = useState({});

    useEffect(() => {
        fetch('/getFeelings')
            .then(res => res.json())
            .then(data => setFeelings(data.feelings))
            .catch(err => console.error("Erreur en récupérant les émotions :", err));
    }, []);

    const handleInputChange = (index, value) => {
        setModifiedFeelings(prev => ({ ...prev, [index]: value }));
    };

    const updateFeeling = async (index) => {
        const newFeeling = modifiedFeelings[index];
        if (!newFeeling) return;

        try {
            const res = await fetch('/updateFeeling', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ index, newFeeling }),
            });

            const data = await res.json();
            if (res.ok) {
                const newFeelings = [...feelings];
                newFeelings[index] = newFeeling;
                setFeelings(newFeelings);
                setEditingIndex(null);
                alert("Feeling mis à jour !");
            } else {
                alert("Erreur : " + data.message);
            }
        } catch (err) {
            console.error("Erreur en mettant à jour le feeling :", err);
        }
    };

    return (
        <div className="container">
            <div className="header">
                <h1>
                    <span>M</span><span>y</span><span>D</span><span>a</span><span>y</span>
                </h1>
            </div >

            <h5>Settings</h5>
            <p>Historique des émotions :</p>
            <ul>
                {feelings.map((f, index) => (
                    <li key={index}>
                        {editingIndex === index ? (
                            <>
                                <input
                                    type="text"
                                    defaultValue={f}
                                    onChange={(e) => handleInputChange(index, e.target.value)}
                                />
                                <button onClick={() => updateFeeling(index)}>Enregistrer</button>
                                <button onClick={() => setEditingIndex(null)}>Annuler</button>
                            </>
                        ) : (
                            <>
                                {f}
                                <button onClick={() => setEditingIndex(index)}>Modifier</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>

            <Link to="/">
                <button type="button" className="submit-button">
                    Home Page
                </button>
            </Link>
            <p className="droits">
                © 2025 myDay. Tous droits réservés. Cette application, ainsi que l’ensemble de son contenu,
                est protégée par les lois en vigueur relatives à la propriété intellectuelle.
                Les données qu’elle contient sont chiffrées afin d’en garantir la sécurité.
            </p>
        </div>
    );
}

export default Settings;
