import React, { useEffect, useState } from 'react';
import './styles/Home.css';
import './styles/Mobile.css';
import { Link } from 'react-router-dom';

function Settings() {
    const [feelings, setFeelings] = useState([]);
    const [editingIndex, setEditingIndex] = useState(null);
    const [modifiedFeelings, setModifiedFeelings] = useState({});

    useEffect(() => {
        fetch('/getFeelings')
            .then(res => res.json())
            .then(data => setFeelings(data.feelings))
            .catch(err => console.error("Erreur en récupérant les émotions :", err));
    }, []);

    const handleInputChange = (index, value) => {
        setModifiedFeelings(prev => ({
            ...prev,
            [index]: value
        }));
    };

    const updateFeeling = (index) => {
        const newFeeling = modifiedFeelings[index];
        if (!newFeeling) return;

        fetch('/updateFeeling', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ index, newFeeling })
        })
        .then(res => res.json())
        .then(data => {
            if (data.success) {
                const updatedFeelings = [...feelings];
                updatedFeelings[index] = newFeeling;
                setFeelings(updatedFeelings); // Mettre à jour les émotions avec le nouveau feeling
                setEditingIndex(null);  // Annuler l'édition après la mise à jour
                setModifiedFeelings({}); // Réinitialiser l'état des modifications
            } else {
                console.error("Erreur côté serveur :", data.message);
            }
        })
        .catch(err => console.error("Erreur en mettant à jour :", err));
    };

    const handleCancelEdit = () => {
        setEditingIndex(null); // Annuler l'édition et revenir à l'affichage original
        setModifiedFeelings({}); // Réinitialiser les modifications
    };

    return (
        <div className="container">
            <div className="header">
                <h1>
                    <span>M</span><span>y</span><span>D</span><span>a</span><span>y</span>
                </h1>
            </div>

            <h5>Settings</h5>
            <p>Historique des émotions :</p>
            <ol>
                {feelings.map((f, index) => (
                    <li key={index}>
                        {editingIndex === index ? (
                            <>
                                <input
                                    type="text"
                                    value={modifiedFeelings[index] || f} // Afficher la valeur modifiée ou la valeur actuelle
                                    onChange={(e) => handleInputChange(index, e.target.value)}
                                />
                                <button onClick={() => {
                                    if (window.confirm("Es-tu sûr de vouloir remplacer cette émotion ? Cela supprimera définitivement l'ancienne.")) {
                                        updateFeeling(index);
                                    }
                                }}>Enregistrer</button>
                                <button onClick={handleCancelEdit}>Annuler</button>
                            </>
                        ) : (
                            <>
                                {f}
                                <button onClick={() => setEditingIndex(index)}>Modifier</button>
                            </>
                        )}
                    </li>
                ))}
            </ol>

            <Link to="/">
                <button type="button" className="submit-button">Home Page</button>
            </Link>

            <p className="droits">
                © 2025 myDay. Tous droits réservés. Cette application, ainsi que l’ensemble de son contenu, est protégée par les lois en vigueur relatives à la propriété intellectuelle. Les données qu’elle contient sont chiffrées afin d’en garantir la sécurité.
            </p>
        </div>
    );
}

export default Settings;
