import './styles/Home.css';
import './styles/Mobile.css';

function Login() {
    const [pseudo, setPseudo] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
    
        if (!acceptTerms) {
          alert("Vous devez accepter les conditions d'utilisation.");
          return;
        }
    
        // Ici, tu peux envoyer les données au backend ou gérer la logique de création du compte
        const userData = {
          pseudo,
          password,
        };
        
        console.log('User Data:', userData);
        // Effectuer l'envoi de ces données à l'API ou base de données
      };

    return (
        <div className="container">
            <div className="header">
                <h1>myDay</h1>
            </div>
            <form onSubmit={handleSubmit} style={{ maxWidth: '500px', margin: '0 auto', padding: '1.5rem', backgroundColor: '#f9f9f9', borderRadius: '8px', boxShadow: '0 2px 5px rgba(0, 0, 0, 0.1)' }}>
                <div style={{ marginBottom: '1rem' }}>
                    <label htmlFor="pseudo" style={{ display: 'block', fontSize: '1rem', marginBottom: '0.5rem' }}>Pseudo</label>
                    <input
                    type="text"
                    id="pseudo"
                    name="pseudo"
                    value={pseudo}
                    onChange={(e) => setPseudo(e.target.value)}
                    required
                    placeholder="Choisissez un pseudo"
                    style={{ width: '100%', padding: '0.75rem', fontSize: '1rem', borderRadius: '5px', border: '1px solid #ddd' }}
                    />
                </div>
                </form>
            <hr class="hr" />
            <h3>Ma gratitude du jour</h3>
            <div className="regles">
                {/* <label for="horns"> En cochant cette case, vous acceptez nos <a href="/conditions-d-utilisation" target="_blank">Conditions d'utilisation</a> et <a href="/politique-de-confidentialite" target="_blank">Politique de confidentialité</a> </label> */}
                <label for="horns"> En cochant cette case, vous acceptez nos conditions d'utilisation et la politique de confidentialité</label>
                <input className="checkBox-regles" type="checkbox" id="horns" name="horns" />
            </div>
            
            <button className="submit-button">Soumettre</button>
        </div>
    );
}

export default Login;
