import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './styles/Home.css';
import './styles/Mobile.css';
import LogoutButton from './LogoutButton';
import SettingsButton from './SettingsButton';
import { Link } from 'react-router-dom';

let feeling1 = "";
let feeling2 = "";
let feeling3 = "";
let feeling4 = "";

function Home() {
  const [theme, setTheme] = useState('colorful'); // Défaut: 'colorful'
  const [rating1, setRating1] = useState(0);
  const [rating2, setRating2] = useState(0);
  const [rating3, setRating3] = useState(0);
  const [rating4, setRating4] = useState(0);
  const [phrase, setPhrase] = useState('');
  const [canSubmit, setCanSubmit] = useState(true);
  const [feelings, setFeelings] = useState(["", "", "", ""]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fonction pour récupérer le thème de l'utilisateur depuis l'API ou du localStorage
    const fetchUserTheme = async () => {
      try {
        const API_URL =
          window.location.hostname === "localhost"
            ? "http://localhost:4000"
            : "https://myday-back.onrender.com";

        // Récupérer les préférences de l'utilisateur (par exemple, depuis l'API)
        const response = await fetch(`${API_URL}/user-theme`, {
          method: "GET",
          headers: { "Content-Type": "application/json" },
          credentials: 'include',
        });

        const data = await response.json();
        if (data && data.theme) {
          setTheme(data.theme); // Mettre à jour le thème avec celui de l'utilisateur
        }
      } catch (error) {
        console.error("Erreur lors de la récupération du thème :", error);
      }
    };

    // Vérifier si un thème est déjà sauvegardé dans le localStorage
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme) {
      setTheme(savedTheme); // Appliquer le thème sauvegardé
    } else {
      fetchUserTheme(); // Sinon, récupérer depuis l'API
    }
  }, []);

  useEffect(() => {
    // Sauvegarder le thème dans le localStorage quand il change
    if (theme) {
      localStorage.setItem('theme', theme);
    }
  }, [theme]);

  const handleRatingChange = (setter) => (newRating) => setter(newRating);
  const handlePhraseChange = (event) => setPhrase(event.target.value);

  const handleClear = () => {
    setRating1(0);
    setRating2(0);
    setRating3(0);
    setRating4(0);
    setPhrase('');
    const checkbox = document.getElementById("regle");
    if (checkbox) {
      checkbox.checked = false;
    }
  };

  const handleSubmit = async () => {
    // Fonction handleSubmit comme précédemment
    const userData = {
      feeling1: rating1,
      feeling2: rating2,
      feeling3: rating3,
      feeling4: rating4,
      phraseGratitude: phrase,
      regle: document.getElementById("regle").checked,
    };

    try {
      const API_URL =
        window.location.hostname === "localhost"
          ? "http://localhost:4000"
          : "https://myday-back.onrender.com";

      const response = await fetch(`${API_URL}/`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(userData),
        credentials: 'include',
      });

      const data = await response.json();
      console.log("Réponse du serveur :", data);

      if (data) {
        handleClear();
        const currentDate = new Date();
        const currentDateWithoutTime = new Date(currentDate.getFullYear(), currentDate.getMonth(), currentDate.getDate());
        localStorage.setItem('lastSubmissionDate', currentDateWithoutTime.toISOString());
        setCanSubmit(false);
        navigate('/Historique');
      }
    } catch (error) {
      console.error("Erreur lors de l'envoi :", error);
    }
  };

  return (
    <div className={`container ${theme}`}>
      <div className="header">
        <h1>
          <span>M</span><span>y</span><span>D</span><span>a</span><span>y</span>
        </h1>
      </div>

      <form onSubmit={(e) => e.preventDefault()}>
        {[ 
          { title: feeling1, rating: rating1, setRating: handleRatingChange(setRating1) },
          { title: feeling2, rating: rating2, setRating: handleRatingChange(setRating2) },
          { title: feeling3, rating: rating3, setRating: handleRatingChange(setRating3) },
          { title: feeling4, rating: rating4, setRating: handleRatingChange(setRating4) }
        ].map((feeling, index) => (
          <div key={index}>
            <h2>{feeling.title}</h2>
            <div className="stars">
              {[1, 2, 3, 4, 5].map((star) => (
                <span
                  key={star}
                  className={`star ${feeling.rating >= star ? 'active' : ''}`}
                  onClick={() => feeling.setRating(star)}
                >
                  &#9733;
                </span>
              ))}
            </div>
          </div>
        ))}

        <hr className="hr" />
        <h3>Mon mantra du jour</h3>
        <textarea
          className="phrase-input"
          placeholder="Ajoutez une phrase ou une réflexion..."
          value={phrase}
          id="phraseGratitude"
          onChange={handlePhraseChange}
        ></textarea>

        <div className="regles">
          <label htmlFor="regles">Règles :</label>
          <input
            className="checkBox-regles"
            type="checkbox"
            id="regle"
            name="regle"
          />
        </div>

        <div className="boutton-clear-submit-index">
          <button
            type="button"
            className="submit-button-clear"
            onClick={handleClear}>
            Effacer
          </button>
          <button
            type="submit"
            className="submit-button"
            onClick={handleSubmit}
          >
            Soumettre
          </button>
        </div>

        <hr className="hr" />

        <div className="settings"> 
          <div className='button-container'>
            <SettingsButton />
          </div>
          <Link to="/Historique">
            <button type="button" className="submit-button">
                Historique
            </button>
          </Link>
          <div className='button-container'>
              <LogoutButton />
          </div>
        </div>
      </form>
      <p className ="droits">© 2025 myDay. Tous droits réservés.</p>
    </div>
  );
}

export default Home;
