                const themeData = await themeResponse.json();
                if (themeData.success && themeData.theme) {
                    // Appliquer le thÃ¨me et le stocker dans localStorage
                    document.body.classList.add(themeData.theme);
                    localStorage.setItem('theme', themeData.theme);  // Sauvegarde du thÃ¨me dans localStorage
                }

                // Rediriger aprÃ¨s avoir appliquÃ© le thÃ¨me
                window.location.href = data.redirectUrl;
            } else {
                setErrorMessage(data.errorMessage);
            }
