const filtrerDonneesParTemps = (data, range) => {
  const maintenant = new Date();
  let limite;

  switch (range) {
    case '24heures':
      limite = new Date(maintenant.setHours(maintenant.getHours() - 24));
      break;
    case '1semaine':
      limite = new Date(maintenant.setDate(maintenant.getDate() - 7));
