import React, { useState } from 'react';
import './styles/Home.css'; // Import du CSS
import './styles/Mobile.css'

function Home() {
    const [rating, setRating] = useState(0); // L'état de la note
    const [phrase, setPhrase] = useState(''); // L'état de la phrase
  
    const handleRatingChange = (newRating) => {
      setRating(newRating);
    };
  
    const handlePhraseChange = (event) => {
      setPhrase(event.target.value);
    };
  
    return (
        
      <div className="container">
        <h1>Hello</h1>
        <h1>Feeling 1</h1>
        
        <div className="stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${rating >= star ? 'active' : ''}`}
              onClick={() => handleRatingChange(star)}
            >
              &#9733;
            </span>
          ))}
        </div>
        <h1>Feeling 1</h1>
        <div className="stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${rating >= star ? 'active' : ''}`}
              onClick={() => handleRatingChange(star)}
            >
              &#9733;
            </span>
          ))}
        </div>
        <h1>Feeling 1</h1>
        <div className="stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${rating >= star ? 'active' : ''}`}
              onClick={() => handleRatingChange(star)}
            >
              &#9733;
            </span>
          ))}
        </div>
        <h1>Feeling 1</h1>
        <div className="stars">
          {[1, 2, 3, 4, 5].map((star) => (
            <span
              key={star}
              className={`star ${rating >= star ? 'active' : ''}`}
              onClick={() => handleRatingChange(star)}
            >
              &#9733;
            </span>
          ))}
        </div>
    
        <textarea
          className="phrase-input"
          placeholder="Ajoutez une phrase ou une réflexion..."
          value={phrase}
          onChange={handlePhraseChange}
        ></textarea>
  
        <button className="submit-button">Soumettre</button>
      </div>
    );
  }

export default Home;
