import React, { useState } from 'react';
import './styles/Home.css';
import './styles/Mobile.css';

function Login() {
    const [pseudo, setPseudo] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
    
        // Ici, tu peux envoyer les données au backend ou gérer la logique de création du compte
        const userData = {
          pseudo,
          password,
        };
        
        console.log('User Data:', userData);
        // Effectuer l'envoi de ces données à l'API ou base de données
      };

    return (
        <div className="container">
            <div className="header">
                <h1>myDay</h1>
            </div>
            <form onSubmit={handleSubmit}>
                <h4>
                    <label htmlFor="pseudo" >Pseudo : </label>
                    <input className="login-input"
                    type="text"
                    id="pseudo"
                    name="pseudo"
                    value={pseudo}
                    onChange={(e) => setPseudo(e.target.value)}
                    required
                    placeholder="Entrez un pseudo"
                    />
                </h4>
                <h3>
                    <label htmlFor="mot de pass" >Mot de pass : </label>
                    <input className="login-input"
                    type="password"
                    id="password"
                    name="password"
                    value={pseudo}
                    onChange={(e) => setPseudo(e.target.value)}
                    required
                    placeholder="Entrez votre mot de pass"
                    />
                </h3>
                </form>
            <hr class="hr" />
            
            <button className="submit-button">Soumettre</button>
        </div>
    );
}

export default Login;
