import React, { useState } from 'react';
import './styles/Home.css';
import './styles/Mobile.css';

function Login() {

    return (
        <div className="container">
            <div className="header">
                <h1>myDay</h1>
            </div>
            <div className="form-group">
                <label for="pseudo">Pseudo</label>
             <input type="text" id="pseudo" name="pseudo" required placeholder="Choisissez un pseudo" className="input-field"/>
        </div>
            <hr class="hr" />
            <h3>Ma gratitude du jour</h3>
            <div className="regles">
                {/* <label for="horns"> En cochant cette case, vous acceptez nos <a href="/conditions-d-utilisation" target="_blank">Conditions d'utilisation</a> et <a href="/politique-de-confidentialite" target="_blank">Politique de confidentialité</a> </label> */}
                <label for="horns"> En cochant cette case, vous acceptez nos conditions d'utilisation et la politique de confidentialité</label>
                <input className="checkBox-regles" type="checkbox" id="horns" name="horns" />
            </div>
            
            <button className="submit-button">Soumettre</button>
        </div>
    );
}

export default Login;
