import React, { useEffect, useState } from 'react';
import './styles/Home.css';
import './styles/Mobile.css';
import { Link } from 'react-router-dom';

function Settings() {

    const [emotionsList,setEmotionsList]=useState([])
    useEffect(() => {
        fetch('/api/emotions')
            .then(res => res.json())
            .then(data => setEmotionsList(data))
            .catch(err => console.error("Erreur en récupérant les émotions :", err));
    }, []);
   
    return (
        <div className="container">
            <div className="header">
            <h1>
                <span>M</span><span>y</span><span>D</span><span>a</span><span>y</span>
                </h1>
            </div >
            
            <h5>Settings</h5>
            <p>Historique des émotions :</p>
            {emotionsList.length > 0 ? (
                <ul>
                    {emotionsList.map((entry, index) => (
                        <li key={index}>
                            <strong>{new Date(entry.userLocalDate).toLocaleDateString()} :</strong><br/>
                            {entry.feeling1}, {entry.feeling2}, {entry.feeling3}, {entry.feeling4}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>Aucune émotion enregistrée.</p>
            )}

            <Link to="/">
              <button type="button" className="submit-button">
                  Home Page
              </button>
             </Link>
             <p className ="droits">© 2025 myDay. Tous droits réservés.
             Cette application, ainsi que l’ensemble de son contenu, est protégée par les lois en vigueur relatives à la propriété intellectuelle. Les données qu’elle contient sont chiffrées afin d’en garantir la sécurité. </p>   
        </div>
    );
}

export default Settings;
