import React, { useState } from 'react';
import './styles/Home.css';
import './styles/Mobile.css';

function Home() {
    const [rating1, setRating1] = useState(0);
    const [rating2, setRating2] = useState(0);
    const [rating3, setRating3] = useState(0);
    const [rating4, setRating4] = useState(0);
    const [phrase, setPhrase] = useState('');

    const handleRatingChangeF1 = (newRating) => {
        setRating1(newRating);
    };

    const handleRatingChangeF2 = (newRating) => {
        setRating2(newRating);
    };

    const handleRatingChangeF3 = (newRating) => {
        setRating3(newRating);
    };

    const handleRatingChangeF4 = (newRating) => {
        setRating4(newRating);
    };

    const handlePhraseChange = (event) => {
        setPhrase(event.target.value);
    };

    const handleClear = () => {
        setRating1(0);
        setRating2(0);
        setRating3(0);
        setRating4(0);
        setPhrase('');
    };

    const handleSubmit = async (e)=>{
        e.prevenDefault();
        const userData = {
            feeling1,
            feeling2,
            feeling3,
            feeling4,
            phraseGratitude,
            regle
        };
    }

    return (
        <div className="container">
            <div className="header">
                <h1>
                <span>M</span><span>y</span><span>D</span><span>a</span><span>y</span>
                </h1>
            </div>

            {[ 
                { value:{feeling1}, id:"felling1", title: "Joie", rating: rating1, setRating: handleRatingChangeF1 }, 
                { value:{feeling2}, id:"felling2", title: "Stress", rating: rating2, setRating: handleRatingChangeF2 }, 
                { value:{feeling3}, id:"felling3", title: "Colère", rating: rating3, setRating: handleRatingChangeF3 }, 
                { value:{feeling4}, id:"felling4", title: "Légèreté", rating: rating4, setRating: handleRatingChangeF4 }
            ].map((feeling, index) => (
                <div key={index}>
                    <h2>{feeling.title}</h2>
                    <div className="stars">
                        {[1, 2, 3, 4, 5].map((star) => (
                            <span
                                key={star}
                                className={`star ${feeling.rating >= star ? 'active' : ''}`}
                                onClick={() => feeling.setRating(star)}
                            >
                                &#9733;
                            </span>
                        ))}
                    </div>
                </div>
            ))}

            <hr class="hr" />
            <h3>Ma gratitude du jour</h3>
            <textarea
                className="phrase-input"
                placeholder="Ajoutez une phrase ou une réflexion..."
                value={phrase}
                id="phraseGratitude"
                onChange={handlePhraseChange}
            ></textarea>
            <div className="regles">
                <label for="regles">Régles : </label>
                <input className="checkBox-regles" type="checkbox" 
                id="regle" name="regle" />
            </div>
            <div className='boutton-clear-submit-index'>
                <button 
                    type="submit"
                    className="submit-button-clear"
                    onClick={handleClear}>
                    Effacer
                </button>
                <button 
                    type="submit"
                    className="submit-button">
                    Soumettre
                </button>  
            </div>

            <hr class="hr" />

            <button 
                type="submit"
                className="submit-button">
                Historique
            </button>
        </div>
    );
}

export default Home;
